import React, { useState } from "react";
import axios from "axios";

function VaultViewer() {
  const [address, setAddress] = useState("0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266");
  const [password, setPassword] = useState("test1234");
  const [vault, setVault] = useState("");

  const handleFetch = async () => {
    try {
      const res = await axios.post("http://localhost:3001/api/vault/decrypt", {
        address,
        password
      });

      setVault(res.data.decrypted);
    } catch (err) {
      setVault("❌ Error: " + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div>
      <h2>Access Your Vault</h2>
      <input value={address} onChange={(e) => setAddress(e.target.value)} />
      <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" />
      <button onClick={handleFetch}>Decrypt Vault</button>
      <pre>{vault}</pre>
    </div>
  );
}

export default VaultViewer;
