console.log("⚡ Using Axios in VaultViewer");
import React, { useState } from "react";
import axios from "axios";
console.log("⚡ Using Axios in VaultViewer");


function VaultViewer() {
  const [address, setAddress] = useState("");
  const [password, setPassword] = useState("");
  const [vault, setVault] = useState("");

  const handleFetch = async () => {
    try {
      const res = await axios.post("http://localhost:3001/api/vault/decrypt", {
        address,
        password
      });
      setVault(res.data.decrypted);
    } catch (err) {
      setVault("❌ Error: " + (err.response?.data?.error || err.message));
    }
  };

  return (
    <div>
      <h2>Access Your Vault</h2>
      <input placeholder="Wallet Address" value={address} onChange={(e) => setAddress(e.target.value)} />
      <input placeholder="Master Password" value={password} onChange={(e) => setPassword(e.target.value)} type="password" />
      <button onClick={handleFetch}>Decrypt Vault</button>
      <pre>{vault}</pre>
    </div>
  );
}

export default VaultViewer;
